package com.flowcontrol;

public class IfEx6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		

			if(args.length==0)
			{
				System.out.println("no values");
			}
			else
			{
				for(int i=0;i<args.length;i++)
				{
					System.out.print(args[i]+"\t");
				}
			}
			
		

	}

}
